example.code:
using System;
 using System.Collections;
 using System.Threading;
 using Microsoft.SPOT;
 using Microsoft.SPOT.Presentation;
 using Microsoft.SPOT.Presentation.Controls;
 using Microsoft.SPOT.Presentation.Media;
 using Microsoft.SPOT.Touch;

 using Gadgeteer.Networking;
 using GT = Gadgeteer;
 using GTM = Gadgeteer.Modules;
 using Gadgeteer.Modules.GHIElectronics;

 namespace TestApp
 {
     public partial class Program
     {
         void ProgramStarted()
         {
             parallelCNC.ConfigurePins(/*Configue the pins here according to the board you are using*/);

             parallelCNC.MoveMotor(GTM.GHIElectronics.ParallelCNC.Axis.X, 1000, true);
             parallelCNC.MoveMotor(GTM.GHIElectronics.ParallelCNC.Axis.Y, 1500, true);
             parallelCNC.MoveMotor(GTM.GHIElectronics.ParallelCNC.Axis.Z, 2000, true);

             Thread.Sleep(2000);

             parallelCNC.MoveMotor(GTM.GHIElectronics.ParallelCNC.Axis.X, 1000, false);
             parallelCNC.MoveMotor(GTM.GHIElectronics.ParallelCNC.Axis.Y, 1500, false);
             parallelCNC.MoveMotor(GTM.GHIElectronics.ParallelCNC.Axis.Z, 2000, false);
         }
     }
 }
